import { useState } from "react"

export default function Posts() {
    const [posts, setPosts] = useState();

    return (
        <>
            {
                posts.length === 0 ?
                    <p>There are no blog posts</p>
                    :
                    posts.map(post => {
                        return (
                            <p key={post.id}>
                                <b>{post.author.username}</b> &mdash; {post.timestamp}
                                <br />
                                {post.text}
                            </p>
                        )
                    })
            }
        </>
    )
}
