export default function App() {
  const posts = [
    {
      id: 1,
      text: 'Hello World',
      timestamp: 'a few seconds ago',
      author: {
        username: 'jane.doe',
      },
    },
    {
      id: 2,
      text: 'Second post',
      timestamp: 'a minute ago',
      author: {
        username: 'john.doe',
      },
    },
    {
      id: 2,
      text: 'Third post',
      timestamp: 'a minute ago',
      author: {
        username: 'john.doe',
      },
    }
  ]

  return (
    <>
      <h1>Buddy</h1>
      {posts.map(post => {
        return (
          <p>
            <b>{post.author.username}</b> &mdash; {post.timestamp}
            <br />
            {post.text}
          </p>
        )
      })}
    </>
  );
}
